def _expand_attr_map(
        attr_map: Tuple[str, ...]
) -> Generator[_AttrMapping, None, None]:
    """Generator that expands the given attr_map and yields an _AttrMapping
    named tuple.

    An attr_map is a tuple with each row containing a :term:`foreign-name`
    which is a specially formatted string.
    """
    for foreign_name in attr_map:
        parts = foreign_name.split(':')
        if len(parts) == 1:
            yield _AttrMapping(foreign_name, None, None)
        elif len(parts) == 2:
            yield _AttrMapping(parts[0], parts[1], None)
        elif len(parts) == 3:
            yield _AttrMapping(parts[0], parts[1], parts[2])
        else:
            raise ValueError(f"Invalid foreign-name format: {foreign_name}")

def as_escaped_unicode_literal(
        text: str
) -> str:
    """Convert the given ``text``  into a string of escaped Unicode
    hexadecimal.

    Args:
    text (:obj:`str`): The string to convert.

    :rtype:
    :obj:`str`

    A string with each character of the given ``text`` converted
    into an escaped Python literal.

    Example:
    >>> from flutils.strutils import as_escaped_unicode_literal
    >>> t = '1. '
    >>> as_literal(t)
    '\\\\x31\\\\x2e\\\\u2605\\\\x20\\\\U0001f6d1'
    """
    def escape_char(c):
        code = ord(c)
        if code <= 0xFF:
            return '\\\\x{:02x}'.format(code)
        elif code <= 0xFFFF:
            return '\\\\u{:04x}'.format(code)
        else:
            return '\\\\U{:08x}'.format(code)

    return ''.join(escape_char(c) for c in text)

def encode(
        text: _Str,
        errors: _Str = 'strict'
) -> Tuple[bytes, int]:
    """Convert a :obj:`str`, that may contain escaped utf8 hexadecimal, to
    bytes of escaped utf8 hexadecimal.

    Args:
    text (str or :obj:`~UserString`): The string input.
    errors (str or :obj:`~UserString`): The error checking level.

    Returns:
    bytes: The given ``text`` converted into escaped utf8 bytes.
    int: The number of given ``text`` characters consumed

    Raises:
    UnicodeEncodeError: if the given ``text`` contains escaped
    utf8 hexadecimal that references invalid utf8 bytes.
    """
    import re

    result = bytearray()
    i = 0
    n = len(text)

    while i < n:
        if text[i] == '\\' and i + 3 < n and text[i+1] == 'x':
            # Try to parse escaped hex byte
            hex_part = text[i+2:i+4]
            try:
                byte_val = int(hex_part, 16)
            except ValueError:
                # Not a valid hex, treat it as literal backslash
                if errors == 'strict':
                    raise UnicodeEncodeError(
                        "utf-8", text, i, i+4,
                        "invalid escaped hex byte"
                    )
                elif errors == 'replace':
                    result.extend(b'?')
                    i += 4
                    continue
                elif errors == 'ignore':
                    i += 4
                    continue
                else:
                    raise UnicodeEncodeError(
                        "utf-8", text, i, i+4,
                        "invalid escaped hex byte"
                    )
            # Validate the byte as part of a valid UTF-8 sequence
            candidate = bytes(result) + bytes([byte_val])
            # We won't fully decode here, but ensure valid UTF-8 prefix
            # Attempt to decode candidate to check validity
            try:
