            except UnicodeDecodeError as e:
                if errors == 'strict':
                    raise UnicodeEncodeError(
                        "utf-8", text, i, i+4,
                        "invalid escaped utf-8 bytes"
                    )
                elif errors == 'replace':
                    result.extend(b'?')
                    i += 4
                    continue
                elif errors == 'ignore':
                    i += 4
                    continue
                else:
                    raise UnicodeEncodeError(
                        "utf-8", text, i, i+4,
                        "invalid escaped utf-8 bytes"
                    )
            result.append(byte_val)
            i += 4
        else:
