LOCAL_TIMEOUT = 5

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_1():
    assert extract_regex(r'<a.*?href="(.*?)">',
                          '<a href="/path1">text1</a> \
                          <a href="/path2">text2</a>') == ['/path1', '/path2']

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_4():
    assert extract_regex(r'<a.*?\s*href="(.*?)">',
                          '<a href="/path1">text1</a> \
                          <a   href="/path2">text2</a>') == ['/path1', '/path2']

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_5():
    assert extract_regex(r'(?P<extract>foo)', 'foobar', encoding='latin1')[0] == 'foo'

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_9():
    assert extract_regex(u'(?P<extract>hello)', u'hello') == [u'hello']

