from twisted.internet import defer, reactor
from twisted.python import failure

def process_chain(callbacks, input, *a, **kw):
    """Return a Deferred built by chaining the given callbacks"""
    def mustbe_deferred(f, *args, **kwargs):
        try:
            result = f(*args, **kwargs)
        except Exception:
            d = defer.Deferred()
            reactor.callLater(0, d.errback, failure.Failure())
            return d
        else:
            if isinstance(result, defer.Deferred):
                return result
            else:
                d = defer.Deferred()
                reactor.callLater(0, d.callback, result)
                return d

    d = defer.succeed(input)
    for callback in callbacks:
        def call_callback(res, cb=callback):
            return mustbe_deferred(cb, res, *a, **kw)
        d = d.addCallback(call_callback)
    return d
